  children = 'Trimite', 
  loadingText = 'Se trimite...', 
  ...props 
}) => (
  <ButtonLoading
    type="submit"
    variant="primary"
    loadingText={loadingText}
    {...props}
  >
    {children}
  </ButtonLoading>
);

