import React from 'react';

const Loading = ({ 
  size = 'medium', 
  color = 'primary', 
  message = 'Loading...', 
  fullScreen = false,
  className = '' 
}) => {
  // Size variations
  const sizeClasses = {
    small: 'w-4 h-4',
    medium: 'w-8 h-8', 
    large: 'w-12 h-12',
    extra: 'w-16 h-16'
  };

  // Color variations
  const colorClasses = {
    primary: 'border-primary-500',
    secondary: 'border-secondary-500', 
    white: 'border-white',
    gray: 'border-gray-500'
  };

  const spinnerClasses = `
    inline-block
    ${sizeClasses[size]}
    border-4
    border-solid
    ${colorClasses[color]}
    border-t-transparent
    rounded-full
    animate-spin
  `.trim().replace(/\s+/g, ' ');

  const containerClasses = fullScreen
    ? 'fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50'
    : 'flex items-center justify-center p-4';

  return (
    <div 
      className={`${containerClasses} ${className}`}
      role="status"
      aria-live="polite"
      aria-label={message}
    >
      <div className="flex flex-col items-center space-y-2">
        <div 
          className={spinnerClasses}
          aria-hidden="true"
        />
        {message && (
          <span className="text-sm text-gray-600 sr-only">
            {message}
          </span>
        )}
      </div>
    </div>
  );
};

// Alternative spinner designs for variety
export const DotsLoading = ({ className = '' }) => (
  <div className={`flex space-x-1 ${className}`} role="status" aria-label="Loading">
    <div className="w-2 h-2 bg-primary-500 rounded-full animate-bounce"></div>
    <div className="w-2 h-2 bg-primary-500 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
    <div className="w-2 h-2 bg-primary-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
  </div>
);

export const PulseLoading = ({ className = '' }) => (
  <div className={`flex space-x-1 ${className}`} role="status" aria-label="Loading">
    <div className="w-3 h-3 bg-primary-500 rounded-full animate-pulse"></div>
    <div className="w-3 h-3 bg-primary-500 rounded-full animate-pulse" style={{ animationDelay: '0.2s' }}></div>
    <div className="w-3 h-3 bg-primary-500 rounded-full animate-pulse" style={{ animationDelay: '0.4s' }}></div>
  </div>
);

export default Loading;
