import React from 'react';

const ErrorMessage = ({ 
  message, 
  type = 'error', 
  dismissible = false,
  onDismiss,
  className = '',
  icon = true 
}) => {
  if (!message) return null;

  // Type-based styling
  const typeStyles = {
    error: {
      container: 'bg-red-50 border-red-200 text-red-800',
      icon: '❌',
      iconColor: 'text-red-500'
    },
    warning: {
      container: 'bg-yellow-50 border-yellow-200 text-yellow-800',
      icon: '⚠️',
      iconColor: 'text-yellow-500'
    },
    info: {
      container: 'bg-blue-50 border-blue-200 text-blue-800',
      icon: 'ℹ️',
      iconColor: 'text-blue-500'
    },
    success: {
      container: 'bg-green-50 border-green-200 text-green-800',
      icon: '✅',
      iconColor: 'text-green-500'
    }
  };

  const currentStyle = typeStyles[type] || typeStyles.error;

  return (
    <div 
      className={`
        flex items-start p-4 mb-4 border rounded-lg
        ${currentStyle.container}
        ${className}
      `.trim().replace(/\s+/g, ' ')}
      role="alert"
      aria-live="polite"
    >
      {icon && (
        <div className={`flex-shrink-0 mr-3 ${currentStyle.iconColor}`}>
          <span className="text-lg" aria-hidden="true">
            {currentStyle.icon}
          </span>
        </div>
      )}

      <div className="flex-1">
        <p className="text-sm font-medium">
          {message}
        </p>
      </div>

      {dismissible && onDismiss && (
        <button
          type="button"
          className={`
            flex-shrink-0 ml-3 p-1 rounded-md
            ${currentStyle.iconColor}
            hover:bg-opacity-20 hover:bg-current
            focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-current
          `.trim().replace(/\s+/g, ' ')}
          onClick={onDismiss}
          aria-label="Dismiss error message"
        >
          <span className="text-lg" aria-hidden="true">×</span>
        </button>
      )}
    </div>
  );
};

// Specialized error components for common use cases
export const FormError = ({ error, ...props }) => (
  <ErrorMessage 
    message={error} 
    type="error" 
    className="mt-2"
    {...props} 
  />
);

export const APIError = ({ error, retry, ...props }) => (
  <ErrorMessage 
    message={error} 
    type="error" 
    dismissible={true}
    className="mb-4"
    {...props}
  />
);

export const ValidationError = ({ errors, ...props }) => {
  if (!errors || errors.length === 0) return null;

  return (
    <div className="space-y-2">
      {errors.map((error, index) => (
        <ErrorMessage 
          key={index}
          message={error} 
          type="error"
          icon={false}
          className="py-2 text-sm"
          {...props}
        />
      ))}
    </div>
  );
};

export default ErrorMessage;
